import logo from './logo.svg';
import './App.css';
import {IoMdClose} from "react-icons/io"
import { useState } from 'react';

function App() {

  const [addSection, setAddSection] = useState(false)

  const handleSubmit = (e) => {
    e.preventDefault()
  }
  return (
    <>
      <div className="container">
        <button className="btn btn-add" onClick={() => setAddSection(true)}>Create</button>

        {
          addSection && (

          <div className="addContainer">
            <form onSubmit={handleSubmit}>
              <div className='close-btn'><IoMdClose/></div>
              <label htmlFor="vendorName">Vendor Name : </label>
              <input type="text" id="vendorName" vendorName="vendorName"/>

              <label htmlFor="bankAccountNo">Bank Acc No. : </label>
              <input type="text" id="bankAccountNo" bankAccountNo="bankAccountNo"/>

              <label htmlFor="bankName">Bank Name : </label>
              <input type="text" id="bankName" bankAccountNo="bankName"/>

              <label htmlFor="addressLine1">Addrs Line 1 : </label>
              <input type="text" id="addressLine1" addressLine1="addressLine1"/>

              <label htmlFor="addressLine2">Addrs Line 2 : </label>
              <input type="text" id="addressLine2" addressLine2="addressLine2"/>

              <label htmlFor="city">City : </label>
              <input type="text" id="city" city="city"/>

              <label htmlFor="country">Country : </label>
              <input type="text" id="country" country="country"/>

              <label htmlFor="zipCode">ZipCode : </label>
              <input type="text" id="zipCode" zipCode="zipCode"/>

              <button className="btn">Submit</button>

            </form>
          </div>
          )
        }


      </div>
    </>
  );
}

export default App;
