import logo from './logo.svg';
import './App.css';
import {IoMdClose} from "react-icons/io";
import { useState, useEffect } from 'react';
import axios from "axios";

axios.defaults.baseURL = "http://localhost:5050/";

function App() {

  const [addSection, setAddSection] = useState(false)
  const [editSection, setEditSection] = useState(false)
  const [formData, setFormData] = useState({

    vendorName : "",
    bankAccountNo: "",
    bankName: "",
    addressLine1: "",
    addressLine2: "",
    city: "",
    country: "",
    zipCode: "",

  })

  const [dataList, setDataList] = useState([])

  const handleOnChange = (e) => {
    const {value, name} = e.target
    setFormData((preve) => {
      return{
        ...preve,
        [name] : value
      }
    })
  }

  const handleSubmit = async(e) => {
    e.preventDefault()

    const data = await axios.post("/create", formData)
    console.log(data)
    if(data.data.success){
      setAddSection(false)
      alert(data.data.message)
      getFetchData()
    }
  }

  const getFetchData = async()=>{
    const data = await axios.get("/")
    console.log(data)
    if(data.data.success){
      setDataList(data.data.data)
    }
  }

  useEffect(()=>{
    getFetchData()
  }, [])

  const handleDelete = async(id) => {
    const data = await axios.delete("/delete/"+id)

    if(data.data.success){
      getFetchData()
      alert(data.data.message)
    }
  }

  const handleUpdate = async(id) => {

  }

  return (
    <>
      <div className="container">
        <button className="btn btn-add" onClick={() => setAddSection(true)}>Create</button>

        {
          addSection && (

          <div className="addContainer">
            <form onSubmit={handleSubmit}>
              <div className='close-btn' onClick={() => setAddSection(false)}><IoMdClose/></div>
              <label htmlFor="vendorName">Vendor Name : </label>
              <input type="text" id="vendorName" name="vendorName" onChange={handleOnChange}/>

              <label htmlFor="bankAccountNo">Bank Acc No. : </label>
              <input type="text" id="bankAccountNo" name="bankAccountNo" onChange={handleOnChange}/>

              <label htmlFor="bankName">Bank Name : </label>
              <input type="text" id="bankName" name="bankName" onChange={handleOnChange}/>

              <label htmlFor="addressLine1">Addrs Line 1 : </label>
              <input type="text" id="addressLine1" name="addressLine1" onChange={handleOnChange}/>

              <label htmlFor="addressLine2">Addrs Line 2 : </label>
              <input type="text" id="addressLine2" name="addressLine2" onChange={handleOnChange}/>

              <label htmlFor="city">City : </label>
              <input type="text" id="city" name="city" onChange={handleOnChange}/>

              <label htmlFor="country">Country : </label>
              <input type="text" id="country" name="country" onChange={handleOnChange}/>

              <label htmlFor="zipCode">ZipCode : </label>
              <input type="number" id="zipCode" name="zipCode" onChange={handleOnChange}/>

              <button className="btn">Submit</button>

            </form>
          </div>
          )
        }

        <div className='tableContainer'>
          <table>
            <thead>
              <tr>
                <th>Vendor Name</th>
                <th>Bank Acc No.</th>
                <th>Bank Name</th>
                <th>Adrs Line 1</th>
                <th>Adrs Line 2</th>
                <th>City</th>
                <th>Country</th>
                <th>ZipCode</th>
                <th>

                </th>

              </tr>
            </thead>
            <tbody>
              { dataList[0] ? (
                dataList.map((e1) =>{
                  return(
                    <tr>
                      <td>{e1.vendorName}</td>
                      <td>{e1.bankAccountNo}</td>
                      <td>{e1.bankName}</td>
                      <td>{e1.addressLine1}</td>
                      <td>{e1.addressLine2}</td>
                      <td>{e1.city}</td>
                      <td>{e1.country}</td>
                      <td>{e1.zipCode}</td>
                      <td>
                        <button className='btn btn-edit'>Edit</button>
                        <button className='btn btn-delete' onClick={()=>handleDelete(e1._id)}>Delete</button> 
                      </td>
                    </tr>
                  )
                }))
                : (
                  <p style={{textAlign : "center"}}>No data</p>
                )
              }
            </tbody>
          </table>
        </div>


      </div>
    </>
  );
}

export default App;
